/**
 * This package contain class for Process Parameter to get Parameters for Calling Api as Map
 */
package org.thirdparty.constant;

/**
 * To Import Classes to access their functionality
 */
import java.util.Map;

/**
 * 
 * This class work for Process Parameter to get Parameters for Calling Api as
 * Map
 * 
 * @author Ankita Shrothi
 *
 */
public class ProcessParameter {

	/**
	 * Parameter to Store Map
	 */
	private Map<String, Object> maps;

	/**
	 * To get the Map
	 * 
	 * @return maps
	 */
	public Map<String, Object> getMaps() {
		return maps;
	}

	/**
	 * To set the Map
	 * 
	 * @param maps
	 */
	public void setMaps(Map<String, Object> maps) {
		this.maps = maps;
	}

}
